/*
 * Placeholder for Three.js library.  In offline mode the application
 * attempts to load Three.js from `libs/three.min.js`.  Replace this
 * file with the actual three.js distribution (`three.min.js`) to
 * enable offline 3D preview.  When running online the blueprint
 * dynamically imports Three.js from a CDN if `window.THREE` is not
 * defined after loading this file.
 */
;(function () {
  if (typeof window !== 'undefined') {
    if (!window.THREE) {
      console.warn(
        'Three.js stub loaded. To enable 3D preview offline, place the real three.min.js in libs/'
      );
      // Provide an empty object so that checks for window.THREE do not throw
      window.THREE = undefined;
    }
  }
})();